const rawScoreTable = runtimeScene.getVariables().get("RawScoreTable").getAsString()
if (!rawScoreTable) {
    console.error("Oh no no score table!")
    return
}
const CardScores = global.Babblebot.CardScores
const scoreTable = CardScores.parseCsv(rawScoreTable)
global.Babblebot.scoreTable = scoreTable

