const cardTags = eventsFunctionContext.getArgument('CardTags')
const characterTags = eventsFunctionContext.getArgument('CharacterTags')
let nodeTags = eventsFunctionContext.getArgument('NodeTags')

nodeTags = nodeTags.split(/,+/).join(' ')

console.log('calculating score', {
    cardTags,
    characterTags,
    nodeTags
})

eventsFunctionContext.returnValue = (() => {
    const CardScores = global.Babblebot.CardScores
    const scoreTable = global.Babblebot.scoreTable
    if (!(CardScores && scoreTable)) {
        console.error("Oh no no libs or data!", {CardScores, scoreTable})
        return 0
    }
    return CardScores.calculateScore(
        cardTags,
        characterTags,
        nodeTags,
        scoreTable
    )
})()

