const ID = "Card Id"
const TYPE = "Card Type"
const TEXT = "Actual Card Text"
const TAGS = "Card Special Tags"

global.Babblebot.cards.forEach((data) => {
    const obj = runtimeScene.createObject("Card")
    const vars = obj.getVariables()
    vars.get("Id").setString(data[ID])
    vars.get("Type").setString(data[TYPE])
    vars.get("Text").setString(data[TEXT])
    vars.get("Tags").setString(data[TYPE] + ' ' + data[TAGS])
})

const text = eventsFunctionContext.getArgument('Text') || ''
const characterTags = eventsFunctionContext.getArgument('Confidence') || 0

const words = text.split(/\s+/)
const digitWords = (
    words
    .map(
        w => w.split(/./).map(_ => Math.random() < .5 ? '0' : '1').join('')
    )
)
eventsFunctionContext.returnValue = digitWords.join(' ')

const [deck] = objects
const instanceIdVar = deck.getVariables().get('CardInstanceId')
const instanceId = instanceIdVar.getAsNumber()
if (deck.cardInstanceIds.includes(instanceId)) {
    console.warn("Deck already includes card", { instanceId })
}
deck.cardInstanceIds.push(instanceId)

const [deck] = objects
const lastDrawnVar = deck.getVariables().get('LastDrawnInstanceId')
if (deck.cardInstanceIds.length === 0) {
    console.warn('Tried to draw card from empty deck')
    lastDrawnVar.setNumber(-1)
    return
}
const instanceId = deck.cardInstanceIds.pop()
lastDrawnVar.setNumber(instanceId)

