events.inlineCode:
const object = objects[0]
const vars = object.getVariables()
const text = vars.get('LastText').getAsString()
const letterTypeVar = vars.get('LetterType')
const pitchVar = vars.get('Pitch')

letterTypeVar.setString('')
pitchVar.setNumber(1)

const lastLetter = (text[text.length - 1] || '').toLowerCase()
let pitchFactor = 1
if (/[aeiou]/.test(lastLetter)) {
    letterTypeVar.setString('vowel')       
    pitchFactor = 0.5
} else if (/[a-z]/.test(lastLetter)) {
    letterTypeVar.setString('consonant')
    pitchFactor = 0.1
} else {
    return
}

const dist = (
    (1 + lastLetter.charCodeAt(0) - 'a'.charCodeAt(0)) /
    (1 + 'z'.charCodeAt(0) - 'a'.charCodeAt(0)) 
)
pitchVar.setNumber(0.75 + (pitchFactor * dist))
